import { Component, OnInit } from '@angular/core';
import { AppService } from './app.service';

@Component({
	selector: 'app-root',
	templateUrl: './app.component.html',
	styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {

	constructor(private appService: AppService) { }

	title = 'angular-nodejs-example';

	person: any;
    
    connected = false;

	ngOnInit() {
		this.appService.getPerson()
			.subscribe((person: any) => {
				this.person = person;
                this.connected = true;
			});
	}
}
<app-header></app-header>
<div *ngIf="connected; then thenBlock else elseBlock"></div>
<ng-template #thenBlock>
    Hello {{person}}<P>
	It is now up to you!</ng-template>
<ng-template #elseBlock>
    The database is not connected, launch the back-end and refresh this page
</ng-template>
<app-accueil></app-accueil>
